import { useState } from "react";
import BillInput from "./BillInput";
import RatingInput from "./RatingInput";
import BillTotal from "./BillTotal";

export default function App() {
  const [bill, setBill] = useState(0);
  const [rating1, setRating1] = useState("1");
  const [rating2, setRating2] = useState("1");

  function handleSelectRating1(rtng1) {
    setRating1(rtng1);
  }
  function handleSelectRating2(rtng2) {
    setRating1(rtng2);
  }
  function handleChangeBill(bill) {
    setBill(bill);
  }

  return (
    <div className="app">
      <h1>Tip Calculator</h1>
      <BillInput />
      <RatingInput rating={rating1}>How did you like the service? </RatingInput>
      <RatingInput rating={rating2}>
        How did your friend like the service?{" "}
      </RatingInput>
      <BillTotal />
      <button className="resetButton">Reset</button>
    </div>
  );
}
